
public class Chapter4_q4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		

		// variables created
		int kilograms = 25 ;
		int grams = 1000;
		
		System.out.println("Grams     Kilograms" );

		// for loop to print out conversion to grams and 1 -25 kg's 
		for (int i = 1; i <= kilograms; i++) {
			
			System.out.println(grams * i + "      " + i);
		}
	}

}
